import React,{useState} from 'react';
import "./Event.css"
import API from '../../utils/API';

function Event()
{
    const [fields, setFields]= useState(
        {title:''
        ,time:'',
        date:'',
        venue:''
    });

    const saveEvent=(event)=>{
        event.preventDefault();
        API.saveEvent().then(res=>
            this.setFields({fields:{title:'',time:'',date:'',venue:''}}))

    }

    return(
        <div className="container">
            <div className="row justify-content-center">
                <form className="mt-3" onSubmit={saveEvent}>
                    <div className="form-group">
                        <label>Event Name</label>
                        <input type="text" className="form-control"/></div>
                    <div className="form-group">    
                        <label>Event Date</label>
                        <input type="date" className="form-control"/></div>
                        <div className="form-group">    
                        <label>Event Time</label>
                        <input type="time" className="form-control"/></div>
                    <div className="form-group">    
                        <label>Event Venue</label>
                        <textarea type="text" className="form-control"/></div>
                    <button className="btn">Save Event</button>
                </form>

            </div>
        </div>
    )
}

export default Event
