import React,{useState} from 'react'
import "./Seller.css"

function Seller(){

    const [fields, setFields] = useState({

        title:"",
        email:"",
        address:"",
        image:null,
        description:"",
        price:""

    });



    function validateForm() {
        return fields.title.length>0 
        && fields.email.length>0
        && fields.price.length>0
        && fields.description.length>0

      }

      const handleInputChange= e=>{

        e.preventDefault();
        const { name, value } = e.target;
        setFields({ ...fields, [name]: value });

      }

      function handleSubmit(event) {
        event.preventDefault();
        const formData = new FormData()
        formData.append("image",this.state.feilds.image)

        var data={
            title:fields.title,
            email:fields.email,
            description:fields.description,
            price:fields.price
        }
        formData.append("data",data)
        API.postTestData(formData).then(res=>
            console.log(res))

      }

      const handleFilechange=() =>{
        const file = event.target.files[0]
        setFields({image: file });
      }

    return(
        <div className="container">
            <div className="row justify-content-center">
                <form action="/new" method="post" className="sellerform mt-3" enctype="multipart/form-data">
                    <div className="form-group" >
                        <label>Title</label>
                        <input className="form-control" type="text"  name="title" value={fields.title} onChange={handleInputChange}/>
                    </div>
                    <div className="form-group" >
                        <label>Quote Price</label>
                        <input  className="form-control" type="Number" name="price" value={fields.price} onChange={handleInputChange}/>
                    </div>
                    <div className="form-group" >
                        <label className="custom-file-label" htmlFor="inputGroupFile01">
                            Choose file
                        </label>
                        <input  className="form-control" type="file" name="image" id ="inputGroupFile01" value={fields.image} onChange={handleFilechange}/></div>
                    <div className="form-group" >
                        <label>Description</label>
                        <textarea  className="form-control" type="text" name="description" value={fields.description} onChange={handleInputChange}></textarea></div>
                    <div className="form-group" >
                        <label>Email</label>
                        <input  className="form-control" type="text"  name="email" value={fields.email} onChange={handleInputChange} /></div>
                    <div className="form-group" >
                        <label>Address</label>
                        <textarea className="form-control" type="text" name="address" value={fields.address} onChange={handleInputChange} /></div>
                    <button className="btn btn-seller m-2" disabled={!validateForm()}>Add Item</button>

                </form>
            </div>
        </div>
    )

}
export default Seller
