import React, { useState } from "react"
import "./OrderPlace.css"
const sgMail = require('@sendgrid/mail');

sgMail.setApiKey(process.env.SENDGRID_API_KEY);

function OrcerPlace(){

    const [fields,setFields] = useState({
        to:'',
        from:'',
        subject:'',
        message:''

    })

    const handleInputChange= e =>{
        e.preventDefault();
        const { name, value } = e.target;
        setFields({ ...fields, [name]: value });
      }

        const sendEmail=()=>{

            console.log("here")

        // const msg = {
        //     to: 'test@example.com',
        //     from: 'test@example.com',
        //     subject: 'Sending with Twilio SendGrid is Fun',
        //     text: 'and easy to do anywhere, even with Node.js',

        //   };
          sgMail.send(fields);
          //setFields({fields:{to:'',from:'',subject:'',message:''}})

    }


    return(
        <div className="container">
            <div className="row">
                <div className="card card-mail mt-3">
                    <form onSubmit={sendEmail} onChange={handleInputChange}>
                        <div className="form-group m-3">
                            <label className="mr-5">To</label>
                            <input type="text" name="to" value={fields.to}/>
                        </div>
                        <div className="form-group m-3">
                            <label className="mr-4">From</label>
                            <input type="text" name="from" value={fields.from}/>
                        </div>

                        <div className="form-group m-3">
                            <label className="mr-3">Subject</label>
                            <input type="text" name="subject" value={fields.subject}/>
                        </div>

                        <div className="form-group m-3">
                            <label className="mr-3 mb-2">Message</label>
                            <textarea type="text" name="message" value={fields.message}/>
                        </div>

                        <button className="btn m-3" type="button" >Submit</button>

                    </form>
                </div>
            </div>
        </div>

    )

}


export default OrcerPlace
