import React , { useState }  from 'react';
import './SignUp.css'
import API from "../../utils/API"


class SignUp extends React.Component {
	state = {
		fields: { confirmPassword:'', email: '', password: ''}
	}

	onInputChange(event) {
		this.setState({
			fields: {
				...this.state.fields,
				[event.target.name]: event.target.value
			}
		})
	}
    validateForm() {
        return (
          email.length > 0 &&
          password.length > 0 &&
          password === confirmPassword
        );
    }
    handleSubmit(event) {
        event.preventDefault();

        API.saveUser( {
            email: email,
            password:password
          })
          .then(data => {
            console.log(data);
            this.props.history.replace("/login");
          });

    }

    render(){
    return (
    <div className ="container">
        <div className ="row justify-content-center">
            <div className="card m-4 signUp">
            <label className ="ml-2 ml-5 pl-3">Email</label>
                <div className="form-group ml-5 pl-3" id="email">

                    <input 
                        autoFocus
                        type="email"
                        value={email}
                        onChange={e => setEmail(e.target.value)}/>
                </div>
                <label className ="ml-2 ml-5 pl-3">password</label>
                <div className="form-group ml-5 pl-3" id="password">

                    <input 
                        type='password'
                        value={password}
                        onChange={e => setPassword(e.target.value)}/>
                </div>
                <label className ="ml-2 ml-5 pl-3">Confirm Password</label>
                <div className="form-group ml-5 pl-3" id="confirmPwd">

                    <input 
                        type='password' 
                        value={confirmPassword}
                        onChange={e => setConfirmPassword(e.target.value)}/>
                </div>
                <button className="btn btn-custom mx-auto mt-2" disabled={!validateForm()} onSubmit={handleSubmit}>Sign up</button>

            </div>
        </div>
    </div>
    )
}
}

export default SignUp
