import React, { useState } from "react";
import './Login.css'



class LogIn extends React.Component {
	state = {
		fields: { email: '', password: ''}
	}


  validateForm() {
    return email.length > 0 && password.length > 0;
  }

   handleSubmit(event) {
    event.preventDefault();
    this.Auth.login(email, password)
    .then(res => {
      if (res === false) {
        return alert("Sorry those credentials don't exist!");
      }
      this.props.history.replace("/");
    })
    .catch(err => {
      alert(err);
    });

  }

 render(){
  return (
    <div className ="container">
        <div className ="row justify-content-center">
        <div className="card m-4">

            <div className="form-group mt-3" id="email">
            <label className ="ml-2 mr-5">Email</label>
            <input 
                autoFocus
                type="email"
                value={email}
                onChange={e => setEmail(e.target.value)}
            />
            </div>
            <div className="form-group mt-3" id="password">
            <label className ="ml-2 mr-3">Password</label>
            <input
                value={password}
                onChange={e => setPassword(e.target.value)}
                type="password"
            />
            </div>
            <button className='btn btn-custom mx-auto mt-3' disabled={!validateForm()} type="submit"  onSubmit={handleSubmit}>
                Login
            </button>

        </div>
        </div>

        <div className="row justify-content-center">
            <h5> New Customer</h5>
        </div>
        <div className = "row justify-content-center">
            <a href="/signup" className='btn btn-signUp m-3' type="submit">Create an account</a>
        </div>

    </div>
  )};
}

export default Login
