import React from 'react';
import './SignUp.css'
import AuthHelper from '../../AuthHelper';


class SignUp extends React.Component {
	state = {
		confirmPassword:'', email: '', password: ''
	}

	handleInputChange = event => {
        // Getting the value and name of the input which triggered the change
        const value = event.target.value;
        const name = event.target.name;

        // Updating the input's state
        this.setState({

          [name]: value

        });
      };
    validateForm() {
        return (
          this.state.email.length > 0 &&
          this.state.password.length > 0 &&
          this.state.password === this.state.confirmPassword
        );
    }
    handleFormSubmit = event => {
        event.preventDefault();
        var data ={
            email:this.state.email,
            password:this.state.password
        }
        console.log("hi")
        AuthHelper.signUp(data).then(user => {
			this.setState( { email: '', password: '' } )
			if(user) {
				this.props.onSignUpSuccess(user)
				this.props.history.push('/')
			}
		})

    }

    render(){
    return (
    <div className ="container">
        <div className ="row justify-content-center">
            <div className="card m-4 signUp">
                <form onSubmit={this.handleFormSubmit}>
                    <label className ="ml-2 ml-5 pl-3">Email</label>
                        <div className="form-group ml-5 pl-3" id="email">

                            <input 
                                autoFocus
                                type="email"
                                name="email"
                                value={this.state.email}
                                onChange={this.handleInputChange}/>
                        </div>
                    <label className ="ml-2 ml-5 pl-3">password</label>
                    <div className="form-group ml-5 pl-3" id="password">

                        <input 
                            type='password'
                            name="password"
                            value={this.state.password}
                            onChange={this.handleInputChange}/>
                    </div>
                    <label className ="ml-2 ml-5 pl-3">Confirm Password</label>
                    <div className="form-group ml-5 pl-3" id="confirmPwd">

                    <input 
                        type='password'
                        name="confirmPassword" 
                        value={this.state.confirmPassword}
                        onChange={this.handleInputChange}/>
                    </div>
                    <button className="btn btn-custom mx-auto mt-2" disabled={!this.validateForm()}>Sign up</button>
                </form>

            </div>
        </div>
    </div>
    )
}
}

export default SignUp
