import React from "react";
import './Login.css'
import AuthHelper from "../../AuthHelper";



class Login extends React.Component {
	state = {
		fields: { email: '', password: ''}
	}


  validateForm() {
    return this.state.fields.email.length > 0 && this.state.fields.password.length > 0;
  }

  handleInputChange=(event) =>{
		this.setState({
			fields: {
				...this.state.fields,
				[event.target.name]: event.target.value
			}
		})
	}

   handleSubmit=(event) =>{
    event.preventDefault();
    AuthHelper.logIn(this.state.fields).then(user => {
			this.setState({ fields: { email: '', password: '' } })
			if(user) {
				this.props.onLoginSuccess(user)
				this.props.history.push('/')
			}
		})

  }

 render(){
  return (
    <div className ="container">
        <div className ="row justify-content-center">
        <div className="card m-4">

            <div className="form-group mt-3" id="email">
            <label className ="ml-2 mr-5">Email</label>
            <input 
                autoFocus
                type="email"
                value={this.state.fields.email}
                onChange={this.handleInputChange}
            />
            </div>
            <div className="form-group mt-3" id="password">
            <label className ="ml-2 mr-3">Password</label>
            <input
                value={this.state.fields.password}
                onChange={this.handleInputChange}
                type="password"
            />
            </div>
            <button className='btn btn-custom mx-auto mt-3' disabled={!this.validateForm()} type="submit"  onSubmit={this.handleSubmit}>
                Login
            </button>

        </div>
        </div>

        <div className="row justify-content-center">
            <h5> New Customer</h5>
        </div>
        <div className = "row justify-content-center">
            <a href="/signup" className='btn btn-signUp m-3' type="submit">Create an account</a>
        </div>

    </div>
  )};
}

export default Login
