import React,{useState} from 'react'
import "./Seller.css"

function Seller(){

    const [field, setFeild] = useState({

        title:"",
        email:"",
        address:"",
        image:[],
        description:"",
        price:""

    });



    function validateForm() {
        return field.title.length>0 
        && field.email.length>0
        && field.price.length>0
        && field.description.length>0

      }

      const handleInputChange= e=>{

        e.preventDefault();
        const { name, value } = e.target;
        setFields({ ...fields, [name]: value });

      }

      function handleSubmit(event) {
        event.preventDefault();


      }

    return(
        <div className="container">
            <div className="row justify-content-center">
                <form onSubmit={handleSubmit} onChange={handleInputChange} className="sellerform mt-3">
                    <div className="form-group" >
                        <label>Title</label>
                        <input className="form-control" type="text" />
                    </div>
                    <div className="form-group" >
                        <label>Quote Price</label>
                        <input  className="form-control" type="Number" onChange={handleInputChange}/>
                    </div>
                    <div className="form-group" >
                        <label>Image</label>
                        <input  className="form-control" type="file" onChange={handleInputChange}/></div>
                    <div className="form-group" >
                        <label>Description</label>
                        <textarea  className="form-control" type="text" onChange={handleInputChange}></textarea></div>
                    <div className="form-group" >
                        <label>Email</label>
                        <input  className="form-control" type="text" onChange={handleInputChange} /></div>
                    <div className="form-group" >
                        <label>Address</label>
                        <textarea className="form-control" type="text" onChange={handleInputChange} /></div>
                    <button className="btn btn-seller m-2" disabled={!validateForm()}>Add Item</button>

                </form>
            </div>
        </div>
    )

}
export default Seller
