import React,{useState} from 'react'

function Seller(){

    const [field, setFeild] = useState({

        title:"",
        email:"",
        address:"",
        image:[],
        description:"",
        price:""

    });



    function validateForm() {
        return field.title.length>0 
        && field.email.length>0
        && field.price.length>0
        && field.description.length>0

      }

      const handleInputChange= event=>{

        setFeild({search:event.target.value})

      }

      function handleSubmit(event) {
        event.preventDefault();


      }

    return(
        <div className="container">
            <div className="row">
                <form onSubmit={handleSubmit}>
                    <input className="form-control" type="text" value={field.title}>Title</input>
                    <input  className="form-control" type="Number">Quote Price</input>
                    <input  className="form-control" type="file">Image</input>
                    <textarea  className="form-control" type="text">Description</textarea>
                    <input  className="form-control" type="text">Email</input>
                    <textarea className="form-control" type="text">Address</textarea>
                    <button className="btn btn-seller" disabled={!validateForm}>Add Item</button>

                </form>
            </div>
        </div>
    )

}
export default Seller
